INSERT INTO `cn_posts` VALUES ('123', '1', '2016-03-23 10:32:40', '2016-03-23 02:32:40', '&nbsp;\r\n\r\n<span style=\"color: #ff0000;\">1.anti-M13-HRP</span>\r\n\r\nHRP/Anti-M13 Monoclonal Conjugate Horseradish Peroxidase conjugated to anti-M13 Monoclonal Antibody with Mouse ascites fluid Product Specification Sheet Code: 27-9421-01\r\n\r\nhttps://www.gelifesciences.com/gehcls_images/GELS/Related%20Content/Files/1314750913712/litdoc27-9421-01PS%20Rev%20D%202007_20110831032452.pdf\r\n\r\n或者点击这里 <a href=\"https://www.gelifesciences.com/gehcls_images/GELS/Related%20Content/Files/1314750913712/litdoc27-9421-01PS%20Rev%20D%202007_20110831032452.pdf\">下载pdf</a>\r\n<h2 class=\"inspira\">HRP/Anti-M13 Monoclonal Conjugate</h2>\r\nHorseradish Peroxidase (HRP) conjugated to anti-M13 Monoclonal Antibody with mouse ascites fluid.\r\n<ul class=\"ul_style_enabled\">\r\n	<li class=\"ul_style_enabled\">Suitable for ELISA and for screening phage display libraries.</li>\r\n	<li class=\"ul_style_enabled\">Convenient colorimetric detection using ABTS as a substrate.</li>\r\n	<li class=\"ul_style_enabled\">Tested and confirmed for its ability to detect M13 K07 phage in an ELISA.</li>\r\n</ul>\r\nThe anti-M13 monoclonal antibody, purified from BALB/c mouse ascites fluid, reacts specifically with the bacteriophage M13 major coat protein product of gene VIII (gp8). The conjugate is supplied in 0.8 ml of 50% glycerol/0.5 x PBS for standard usage at a 1:5000 dilution (200 μl/microplate well). This provides sufficient HRP/Anti-M13 Monoclonal Conjugate for two hundred detection assays using the supplied protocol.\r\n\r\nHRP/Anti-M13 Monoclonal Conjugate is suitable for screening single chain antibody libraries as part of a phage display system where antibody fragments are expressed as fusions with phage proteins on the phage surface.\r\n\r\n&nbsp;\r\n<h2 class=\"inspira\">HRP/Anti-M13 Monoclonal Conjugate</h2>\r\n<table summary=\"HRP/Anti-M13 Monoclonal Conjugate\">\r\n<tbody>\r\n<tr>\r\n<td width=\"25%\">Complete Packsize</td>\r\n<td width=\"75%\">For 200 plates</td>\r\n</tr>\r\n<tr class=\"oddTableRowColor\">\r\n<td width=\"25%\">Application</td>\r\n<td width=\"75%\">M13 phage detection</td>\r\n</tr>\r\n<tr>\r\n<td width=\"25%\">Storage Conditions</td>\r\n<td width=\"75%\">-20°C</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n&nbsp;', 'HRP/Anti-M13 Monoclonal Conjugate', '', 'publish', 'open', 'open', '', 'hrpanti-m13-monoclonal-conjugate', '', '', '2016-03-23 10:56:51', '2016-03-23 02:56:51', '', '0', 'http://wpcn.dawneve.cc/?p=123', '0', 'post', '', '0');
INSERT INTO `cn_posts` VALUES ('124', '1', '2016-03-23 10:32:41', '2016-03-23 02:32:41', '&nbsp;\r\n\r\n<span style=\"color: #ff0000;\">1.anti-M13-HRP</span>\r\n\r\nHRP/Anti-M13 Monoclonal Conjugate Horseradish Peroxidase conjugated to anti-M13 Monoclonal Antibody with Mouse ascites fluid Product Specification Sheet Code: 27-9421-01\r\n\r\nhttps://www.gelifesciences.com/gehcls_images/GELS/Related%20Content/Files/1314750913712/litdoc27-9421-01PS%20Rev%20D%202007_20110831032452.pdf\r\n\r\n&nbsp;\r\n<h2 class=\"inspira\">HRP/Anti-M13 Monoclonal Conjugate</h2>\r\nHorseradish Peroxidase (HRP) conjugated to anti-M13 Monoclonal Antibody with mouse ascites fluid.\r\n<ul class=\"ul_style_enabled\">\r\n	<li class=\"ul_style_enabled\">Suitable for ELISA and for screening phage display libraries.</li>\r\n	<li class=\"ul_style_enabled\">Convenient colorimetric detection using ABTS as a substrate.</li>\r\n	<li class=\"ul_style_enabled\">Tested and confirmed for its ability to detect M13 K07 phage in an ELISA.</li>\r\n</ul>\r\nThe anti-M13 monoclonal antibody, purified from BALB/c mouse ascites fluid, reacts specifically with the bacteriophage M13 major coat protein product of gene VIII (gp8). The conjugate is supplied in 0.8 ml of 50% glycerol/0.5 x PBS for standard usage at a 1:5000 dilution (200 μl/microplate well). This provides sufficient HRP/Anti-M13 Monoclonal Conjugate for two hundred detection assays using the supplied protocol.\r\n\r\nHRP/Anti-M13 Monoclonal Conjugate is suitable for screening single chain antibody libraries as part of a phage display system where antibody fragments are expressed as fusions with phage proteins on the phage surface.\r\n\r\n&nbsp;\r\n\r\n&nbsp;', 'HRP/Anti-M13 Monoclonal Conjugate', '', 'inherit', 'closed', 'closed', '', '123-revision-v1', '', '', '2016-03-23 10:32:41', '2016-03-23 02:32:41', '', '123', 'http://wpcn.dawneve.cc/?p=124', '0', 'revision', '', '0');
INSERT INTO `cn_posts` VALUES ('125', '1', '2016-03-23 10:56:24', '2016-03-23 02:56:24', '&nbsp;\n\n<span style=\"color: #ff0000;\">1.anti-M13-HRP</span>\n\nHRP/Anti-M13 Monoclonal Conjugate Horseradish Peroxidase conjugated to anti-M13 Monoclonal Antibody with Mouse ascites fluid Product Specification Sheet Code: 27-9421-01\n\nhttps://www.gelifesciences.com/gehcls_images/GELS/Related%20Content/Files/1314750913712/litdoc27-9421-01PS%20Rev%20D%202007_20110831032452.pdf\n\n&nbsp;\n<h2 class=\"inspira\">HRP/Anti-M13 Monoclonal Conjugate</h2>\nHorseradish Peroxidase (HRP) conjugated to anti-M13 Monoclonal Antibody with mouse ascites fluid.\n<ul class=\"ul_style_enabled\">\n	<li class=\"ul_style_enabled\">Suitable for ELISA and for screening phage display libraries.</li>\n	<li class=\"ul_style_enabled\">Convenient colorimetric detection using ABTS as a substrate.</li>\n	<li class=\"ul_style_enabled\">Tested and confirmed for its ability to detect M13 K07 phage in an ELISA.</li>\n</ul>\nThe anti-M13 monoclonal antibody, purified from BALB/c mouse ascites fluid, reacts specifically with the bacteriophage M13 major coat protein product of gene VIII (gp8). The conjugate is supplied in 0.8 ml of 50% glycerol/0.5 x PBS for standard usage at a 1:5000 dilution (200 μl/microplate well). This provides sufficient HRP/Anti-M13 Monoclonal Conjugate for two hundred detection assays using the supplied protocol.\n\nHRP/Anti-M13 Monoclonal Conjugate is suitable for screening single chain antibody libraries as part of a phage display system where antibody fragments are expressed as fusions with phage proteins on the phage surface.\n\n&nbsp;\n<h2 class=\"inspira\">HRP/Anti-M13 Monoclonal Conjugate</h2>\n<table summary=\"HRP/Anti-M13 Monoclonal Conjugate\">\n<tbody>\n<tr>\n<td width=\"25%\">Complete Packsize</td>\n<td width=\"75%\">For 200 plates</td>\n</tr>\n<tr class=\"oddTableRowColor\">\n<td width=\"25%\">Application</td>\n<td width=\"75%\">M13 phage detection</td>\n</tr>\n<tr>\n<td width=\"25%\">Storage Conditions</td>\n<td width=\"75%\">-20°C</td>\n</tr>\n</tbody>\n</table>\n&nbsp;', 'HRP/Anti-M13 Monoclonal Conjugate', '', 'inherit', 'closed', 'closed', '', '123-autosave-v1', '', '', '2016-03-23 10:56:24', '2016-03-23 02:56:24', '', '123', 'http://wpcn.dawneve.cc/?p=125', '0', 'revision', '', '0');
INSERT INTO `cn_posts` VALUES ('126', '1', '2016-03-23 10:56:51', '2016-03-23 02:56:51', '&nbsp;\r\n\r\n<span style=\"color: #ff0000;\">1.anti-M13-HRP</span>\r\n\r\nHRP/Anti-M13 Monoclonal Conjugate Horseradish Peroxidase conjugated to anti-M13 Monoclonal Antibody with Mouse ascites fluid Product Specification Sheet Code: 27-9421-01\r\n\r\nhttps://www.gelifesciences.com/gehcls_images/GELS/Related%20Content/Files/1314750913712/litdoc27-9421-01PS%20Rev%20D%202007_20110831032452.pdf\r\n\r\n或者点击这里 <a href=\"https://www.gelifesciences.com/gehcls_images/GELS/Related%20Content/Files/1314750913712/litdoc27-9421-01PS%20Rev%20D%202007_20110831032452.pdf\">下载pdf</a>\r\n<h2 class=\"inspira\">HRP/Anti-M13 Monoclonal Conjugate</h2>\r\nHorseradish Peroxidase (HRP) conjugated to anti-M13 Monoclonal Antibody with mouse ascites fluid.\r\n<ul class=\"ul_style_enabled\">\r\n	<li class=\"ul_style_enabled\">Suitable for ELISA and for screening phage display libraries.</li>\r\n	<li class=\"ul_style_enabled\">Convenient colorimetric detection using ABTS as a substrate.</li>\r\n	<li class=\"ul_style_enabled\">Tested and confirmed for its ability to detect M13 K07 phage in an ELISA.</li>\r\n</ul>\r\nThe anti-M13 monoclonal antibody, purified from BALB/c mouse ascites fluid, reacts specifically with the bacteriophage M13 major coat protein product of gene VIII (gp8). The conjugate is supplied in 0.8 ml of 50% glycerol/0.5 x PBS for standard usage at a 1:5000 dilution (200 μl/microplate well). This provides sufficient HRP/Anti-M13 Monoclonal Conjugate for two hundred detection assays using the supplied protocol.\r\n\r\nHRP/Anti-M13 Monoclonal Conjugate is suitable for screening single chain antibody libraries as part of a phage display system where antibody fragments are expressed as fusions with phage proteins on the phage surface.\r\n\r\n&nbsp;\r\n<h2 class=\"inspira\">HRP/Anti-M13 Monoclonal Conjugate</h2>\r\n<table summary=\"HRP/Anti-M13 Monoclonal Conjugate\">\r\n<tbody>\r\n<tr>\r\n<td width=\"25%\">Complete Packsize</td>\r\n<td width=\"75%\">For 200 plates</td>\r\n</tr>\r\n<tr class=\"oddTableRowColor\">\r\n<td width=\"25%\">Application</td>\r\n<td width=\"75%\">M13 phage detection</td>\r\n</tr>\r\n<tr>\r\n<td width=\"25%\">Storage Conditions</td>\r\n<td width=\"75%\">-20°C</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n&nbsp;', 'HRP/Anti-M13 Monoclonal Conjugate', '', 'inherit', 'closed', 'closed', '', '123-revision-v1', '', '', '2016-03-23 10:56:51', '2016-03-23 02:56:51', '', '123', 'http://wpcn.dawneve.cc/?p=126', '0', 'revision', '', '0');
